import React from 'react';

const CarroLista = ({ carros, onDeletar, loading }) => {
  if (loading) {
    return (
      <div className="loading">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Carregando...</span>
        </div>
        <p className="mt-2">Carregando carros...</p>
      </div>
    );
  }

  if (carros.length === 0) {
    return (
      <div className="text-center py-4">
        <i className="fas fa-car fa-3x text-muted mb-3"></i>
        <p className="text-muted">Nenhum carro cadastrado ainda.</p>
        <small className="text-muted">
          Use o formul√°rio ao lado para adicionar o primeiro carro.
        </small>
      </div>
    );
  }

  return (
    <div className="carro-lista">
      {carros.map((carro) => (
        <div key={carro.id} className="carro-card card mb-3">
          <div className="card-body">
            <div className="row align-items-center">
              <div className="col-md-8">
                <h6 className="card-title mb-1">
                  <i className="fas fa-car me-2 text-primary"></i>
                  {carro.modelo}
                </h6>
                <p className="card-text text-muted mb-1">
                  <i className="fas fa-tag me-2"></i>
                  Marca: {carro.marca}
                </p>
                <p className="card-text text-muted mb-0">
                  <i className="fas fa-calendar me-2"></i>
                  Ano: {carro.ano}
                </p>
                <small className="text-muted">
                  <i className="fas fa-id-badge me-1"></i>
                  ID: {carro.id}
                </small>
              </div>
              <div className="col-md-4 text-end">
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => {
                    if (window.confirm(`Tem certeza que deseja deletar o carro ${carro.modelo}?`)) {
                      onDeletar(carro.id);
                    }
                  }}
                  title={`Deletar ${carro.modelo}`}
                >
                  <i className="fas fa-trash me-1"></i>
                  Deletar
                </button>
              </div>
            </div>
          </div>
        </div>
      ))}
      
      <div className="mt-3">
        <small className="text-muted">
          <i className="fas fa-info-circle me-1"></i>
          Total de {carros.length} carro(s) cadastrado(s)
        </small>
      </div>
    </div>
  );
};

export default CarroLista;
