import React, { useState } from 'react';

const CarroFormulario = ({ onAdicionar }) => {
  const [formData, setFormData] = useState({
    modelo: '',
    ano: '',
    marca: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Limpar mensagens de erro/sucesso quando o usuário digita
    if (error) setError(null);
    if (success) setSuccess(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validação básica
    if (!formData.modelo.trim()) {
      setError('Modelo é obrigatório');
      return;
    }
    
    if (!formData.ano || formData.ano <= 1900 || formData.ano > 2024) {
      setError('Ano deve ser entre 1900 e 2024');
      return;
    }

    if (!formData.marca.trim()) {
      setError('Marca é obrigatória');
      return;
    }

    setLoading(true);
    setError(null);
    
    try {
      const novoCarro = {
        modelo: formData.modelo.trim(),
        ano: parseInt(formData.ano),
        marca: formData.marca.trim()
      };
      
      await onAdicionar(novoCarro);
      
      // Limpar formulário após sucesso
      setFormData({ modelo: '', ano: '', marca: '' });
      setSuccess('Carro adicionado com sucesso!');
      
      // Limpar mensagem de sucesso após 3 segundos
      setTimeout(() => setSuccess(null), 3000);
      
    } catch (err) {
      setError('Erro ao adicionar carro. Tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {error && (
        <div className="alert alert-danger" role="alert">
          <i className="fas fa-exclamation-triangle me-2"></i>
          {error}
        </div>
      )}
      
      {success && (
        <div className="alert alert-success" role="alert">
          <i className="fas fa-check-circle me-2"></i>
          {success}
        </div>
      )}

      <div className="mb-3">
        <label htmlFor="modelo" className="form-label">
          <i className="fas fa-car me-2"></i>
          Modelo *
        </label>
        <input
          type="text"
          className="form-control"
          id="modelo"
          name="modelo"
          value={formData.modelo}
          onChange={handleChange}
          placeholder="Digite o modelo do carro"
          disabled={loading}
          required
        />
      </div>

      <div className="mb-3">
        <label htmlFor="ano" className="form-label">
          <i className="fas fa-calendar me-2"></i>
          Ano *
        </label>
        <input
          type="number"
          className="form-control"
          id="ano"
          name="ano"
          value={formData.ano}
          onChange={handleChange}
          placeholder="Digite o ano"
          min="1900"
          max="2024"
          disabled={loading}
          required
        />
      </div>

      <div className="mb-3">
        <label htmlFor="marca" className="form-label">
          <i className="fas fa-tag me-2"></i>
          Marca *
        </label>
        <input
          type="text"
          className="form-control"
          id="marca"
          name="marca"
          value={formData.marca}
          onChange={handleChange}
          placeholder="Digite a marca"
          disabled={loading}
          required
        />
      </div>

      <button
        type="submit"
        className="btn btn-primary w-100"
        disabled={loading || !formData.modelo.trim() || !formData.ano || !formData.marca.trim()}
      >
        {loading ? (
          <>
            <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
            Adicionando...
          </>
        ) : (
          <>
            <i className="fas fa-plus me-2"></i>
            Adicionar Carro
          </>
        )}
      </button>

      <div className="mt-3">
        <small className="text-muted">
          <i className="fas fa-info-circle me-1"></i>
          Os dados serão salvos no banco PostgreSQL via API REST
        </small>
      </div>
    </form>
  );
};

export default CarroFormulario;
